import{_ as e,c as a,o as i,d as s}from"./app.ffde2a21.js";const v=JSON.parse('{"title":"DevOps \xB7 CI\xB7CD","description":"","frontmatter":{},"headers":[{"level":2,"title":"DevOps","slug":"devops"},{"level":2,"title":"CI\xB7CD","slug":"ci\xB7cd"},{"level":3,"title":"CI","slug":"ci"},{"level":3,"title":"CD","slug":"cd"},{"level":2,"title":"CI\xB7CD\u3001DevOps \u5173\u7CFB","slug":"ci\xB7cd\u3001devops-\u5173\u7CFB"}],"relativePath":"03_\u8FD0\u7EF4/02_DevOps/01_DevOps \xB7 CI\xB7CD/index.md","lastUpdated":null}'),d={name:"03_\u8FD0\u7EF4/02_DevOps/01_DevOps \xB7 CI\xB7CD/index.md"},t=s('<h1 id="devops-\xB7-ci\xB7cd" tabindex="-1">DevOps \xB7 CI\xB7CD <a class="header-anchor" href="#devops-\xB7-ci\xB7cd" aria-hidden="true">#</a></h1><h2 id="devops" tabindex="-1">DevOps <a class="header-anchor" href="#devops" aria-hidden="true">#</a></h2><p>DevOps \u662F Development\uFF08\u5F00\u53D1\uFF09\u548C Operations\uFF08\u8FD0\u7EF4\uFF09\u7684\u7EC4\u5408\uFF0C\u662F\u4E00\u79CD\u65B9\u6CD5\u8BBA\uFF0C\u662F\u4E00\u7EC4\u8FC7\u7A0B\u3001\u65B9\u6CD5\u4E0E\u7CFB\u7EDF\u7684\u7EDF\u79F0\uFF0C\u7528\u4E8E\u4FC3\u8FDB\u5E94\u7528\u5F00\u53D1\u3001\u5E94\u7528\u8FD0\u7EF4\u548C\u8D28\u91CF\u4FDD\u969C\uFF08QA\uFF09\u90E8\u95E8\u4E4B\u95F4\u7684\u6C9F\u901A\u3001\u534F\u4F5C\u4E0E\u6574\u5408\uFF0C\u4EE5\u671F\u6253\u7834\u4F20\u7EDF\u5F00\u53D1\u548C\u8FD0\u8425\u4E4B\u95F4\u7684\u58C1\u5792\u548C\u9E3F\u6C9F\uFF1B</p><p>DevOps \u662F\u4E00\u79CD\u91CD\u89C6\u8F6F\u4EF6\u5F00\u53D1\u4EBA\u5458\uFF08Dev\uFF09\u548C IT \u8FD0\u7EF4\u6280\u672F\u4EBA\u5458\uFF08Ops\uFF09\u4E4B\u95F4\u6C9F\u901A\u5408\u4F5C\u7684\u6587\u5316\u3001\u8FD0\u52A8\u6216\u60EF\u4F8B\uFF0C\u901A\u8FC7\u81EA\u52A8\u5316\u8F6F\u4EF6\u4EA4\u4ED8\u548C\u67B6\u6784\u53D8\u66F4\u7684\u6D41\u7A0B\uFF0C\u6765\u4F7F\u5F97\u6784\u5EFA\u3001\u6D4B\u8BD5\u3001\u53D1\u5E03\u8F6F\u4EF6\u80FD\u591F\u66F4\u52A0\u5730\u5FEB\u6377\u3001\u9891\u7E41\u548C\u53EF\u9760\uFF1B\u5177\u4F53\u6765\u8BF4\uFF0C\u5C31\u662F\u5728\u8F6F\u4EF6\u4EA4\u4ED8\u548C\u90E8\u7F72\u8FC7\u7A0B\u4E2D\u63D0\u9AD8\u6C9F\u901A\u4E0E\u534F\u4F5C\u7684\u6548\u7387\uFF0C\u65E8\u5728\u66F4\u5FEB\u3001\u66F4\u53EF\u9760\u7684\u7684\u53D1\u5E03\u66F4\u9AD8\u8D28\u91CF\u7684\u4EA7\u54C1\uFF1B</p><p>\u4E5F\u5C31\u662F\u8BF4 DevOps \u662F\u4E00\u7EC4\u8FC7\u7A0B\u548C\u65B9\u6CD5\u7684\u7EDF\u79F0\uFF0C\u5E76\u4E0D\u6307\u4EE3\u67D0\u4E00\u7279\u5B9A\u7684\u8F6F\u4EF6\u5DE5\u5177\u6216\u8F6F\u4EF6\u5DE5\u5177\u7EC4\u5408\uFF1B\u5404\u79CD\u5DE5\u5177\u8F6F\u4EF6\u6216\u8F6F\u4EF6\u7EC4\u5408\u53EF\u4EE5\u5B9E\u73B0 DevOps \u7684\u6982\u5FF5\u65B9\u6CD5\uFF0C\u5176\u672C\u8D28\u662F\u4E00\u6574\u5957\u7684\u65B9\u6CD5\u8BBA\uFF0C\u800C\u4E0D\u662F\u6307\u67D0\u79CD\u6216\u67D0\u4E9B\u5DE5\u5177\u96C6\u5408\uFF0C\u4E0E\u8F6F\u4EF6\u5F00\u53D1\u4E2D\u8BBE\u8BA1\u5230\u7684 OOP\u3001AOP\u3001IOC\uFF08\u6216 DI\uFF09\u7B49\u7C7B\u4F3C\uFF0C\u662F\u4E00\u79CD\u7406\u8BBA\u6216\u8FC7\u7A0B\u6216\u65B9\u6CD5\u7684\u62BD\u8C61\u6216\u4EE3\u79F0\u3002</p><h2 id="ci\xB7cd" tabindex="-1">CI\xB7CD <a class="header-anchor" href="#ci\xB7cd" aria-hidden="true">#</a></h2><h3 id="ci" tabindex="-1">CI <a class="header-anchor" href="#ci" aria-hidden="true">#</a></h3><p>CI \u7684\u82F1\u6587\u540D\u79F0\u662F Continuous Integration\uFF0C\u4E2D\u6587\u7FFB\u8BD1\u4E3A\u6301\u7EED\u96C6\u6210\uFF1BCI \u4E2D\uFF0C\u5F00\u53D1\u4EBA\u5458\u5C06\u4F1A\u9891\u7E41\u5730\u5411\u4E3B\u5E72\u63D0\u4EA4\u4EE3\u7801\uFF0C\u8FD9\u4E9B\u65B0\u63D0\u4EA4\u7684\u4EE3\u7801\u5728\u6700\u7EC8\u5408\u5E76\u5230\u4E3B\u5E72\u524D\uFF0C\u9700\u8981\u7ECF\u8FC7\u7F16\u8BD1\u548C\u81EA\u52A8\u5316\u6D4B\u8BD5\u6D41\u8FDB\u884C\u9A8C\u8BC1\uFF1B</p><p>\u6301\u7EED\u96C6\u6210 CI \u662F\u5728\u6E90\u4EE3\u7801\u53D8\u66F4\u540E\u81EA\u52A8\u68C0\u6D4B\u3001\u62C9\u53D6\u3001\u6784\u5EFA\u548C\u8FDB\u884C\u5355\u5143\u6D4B\u8BD5\u7684\u8FC7\u7A0B\uFF0C\u6301\u7EED\u96C6\u6210\u7684\u76EE\u6807\u662F\u5FEB\u901F\u786E\u4FDD\u5F00\u53D1\u4EBA\u5458\u65B0\u63D0\u4EA4\u7684\u53D8\u66F4\u662F\u597D\u7684\uFF0C\u5E76\u4E14\u9002\u5408\u5728\u4EE3\u7801\u5E93\u4E2D\u8FDB\u4E00\u6B65\u4F7F\u7528\uFF1BCI \u7684\u6D41\u7A0B\u6267\u884C\u548C\u7406\u8BBA\u5B9E\u8DF5\u8BA9\u6211\u4EEC\u53EF\u4EE5\u786E\u5B9A\u65B0\u4EE3\u7801\u548C\u539F\u6709\u4EE3\u7801\u80FD\u5426\u6B63\u786E\u5730\u96C6\u6210\u5728\u4E00\u8D77\u3002</p><h3 id="cd" tabindex="-1">CD <a class="header-anchor" href="#cd" aria-hidden="true">#</a></h3><p>CD \u5BF9\u5E94\u591A\u4E2A\u82F1\u6587\u540D\u79F0\uFF0C\u6301\u7EED\u4EA4\u4ED8 Continuous Delivery \u548C\u6301\u7EED\u90E8\u7F72 Continuous Deployment\uFF0C\u4EE5\u4E0B\u5206\u522B\u4ECB\u7ECD\u3002</p><h4 id="\u6301\u7EED\u4EA4\u4ED8\uFF08continuous-delivery\uFF09" tabindex="-1">\u6301\u7EED\u4EA4\u4ED8\uFF08Continuous Delivery\uFF09 <a class="header-anchor" href="#\u6301\u7EED\u4EA4\u4ED8\uFF08continuous-delivery\uFF09" aria-hidden="true">#</a></h4><p>\u5B8C\u6210 CI \u4E2D\u6784\u5EFA\u53CA\u5355\u5143\u6D4B\u8BD5\u548C\u96C6\u6210\u6D4B\u8BD5\u7684\u81EA\u52A8\u5316\u6D41\u7A0B\u540E\uFF0C\u6301\u7EED\u4EA4\u4ED8\u53EF\u81EA\u52A8\u5C06\u5DF2\u9A8C\u8BC1\u7684\u4EE3\u7801\u53D1\u5E03\u5230\u5B58\u50A8\u5E93\uFF1B\u4E3A\u4E86\u5B9E\u73B0\u9AD8\u6548\u7684\u6301\u7EED\u4EA4\u4ED8\u6D41\u7A0B\uFF0C\u52A1\u5FC5\u8981\u786E\u4FDD CI \u5DF2\u5185\u7F6E\u4E8E\u5F00\u53D1\u7BA1\u9053\uFF0C\u6301\u7EED\u4EA4\u4ED8\u7684\u76EE\u6807\u662F\u62E5\u6709\u4E00\u4E2A\u53EF\u968F\u65F6\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u7684\u4EE3\u7801\u5E93\u3002</p><p>\u5728\u6301\u7EED\u4EA4\u4ED8\u4E2D\uFF0C\u6BCF\u4E2A\u9636\u6BB5\uFF08\u4ECE\u4EE3\u7801\u66F4\u6539\u7684\u5408\u5E76\uFF0C\u5230\u751F\u4EA7\u5C31\u7EEA\u578B\u6784\u5EFA\u7248\u672C\u7684\u4EA4\u4ED8\uFF09\u90FD\u6D89\u53CA\u6D4B\u8BD5\u81EA\u52A8\u5316\u548C\u4EE3\u7801\u53D1\u5E03\u81EA\u52A8\u5316\uFF1B\u5728\u6D41\u7A0B\u7ED3\u675F\u65F6\uFF0C\u8FD0\u7EF4\u56E2\u961F\u53EF\u4EE5\u5FEB\u901F\u3001\u8F7B\u677E\u5730\u5C06\u5E94\u7528\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\u6216\u53D1\u5E03\u7ED9\u6700\u7EC8\u4F7F\u7528\u7684\u7528\u6237\u3002</p><h4 id="\u6301\u7EED\u90E8\u7F72\uFF08continuous-deployment\uFF09" tabindex="-1">\u6301\u7EED\u90E8\u7F72\uFF08Continuous Deployment\uFF09 <a class="header-anchor" href="#\u6301\u7EED\u90E8\u7F72\uFF08continuous-deployment\uFF09" aria-hidden="true">#</a></h4><p>\u5BF9\u4E8E\u4E00\u4E2A\u6210\u719F\u7684 CI/CD \u7BA1\u9053\u6765\u8BF4\uFF0C\u6700\u540E\u7684\u9636\u6BB5\u662F\u6301\u7EED\u90E8\u7F72\uFF1B\u4F5C\u4E3A\u6301\u7EED\u4EA4\u4ED8\uFF08\u81EA\u52A8\u5C06\u751F\u4EA7\u5C31\u7EEA\u578B\u6784\u5EFA\u7248\u672C\u53D1\u5E03\u5230\u4EE3\u7801\u5B58\u50A8\u5E93\uFF09\u7684\u5EF6\u4F38\uFF0C\u6301\u7EED\u90E8\u7F72\u53EF\u4EE5\u81EA\u52A8\u5C06\u5E94\u7528\u53D1\u5E03\u5230\u751F\u4EA7\u73AF\u5883\uFF1B</p><p>\u6301\u7EED\u4EA4\u4ED8\u610F\u5473\u7740\u6240\u6709\u7684\u53D8\u66F4\u90FD\u53EF\u4EE5\u88AB\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\uFF0C\u6301\u7EED\u90E8\u7F72\u610F\u5473\u7740\u6240\u6709\u7684\u53D8\u66F4\u90FD\u4F1A\u88AB\u81EA\u52A8\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\uFF0C\u4F46\u662F\u51FA\u4E8E\u4E1A\u52A1\u8003\u8651\u53EF\u4EE5\u9009\u62E9\u4E0D\u90E8\u7F72\uFF1B\u5982\u679C\u8981\u5B9E\u65BD\u6301\u7EED\u90E8\u7F72\uFF0C\u5FC5\u987B\u5148\u5B9E\u65BD\u6301\u7EED\u4EA4\u4ED8\uFF1B</p><p>\u6301\u7EED\u4EA4\u4ED8\u5E76\u4E0D\u662F\u6307\u8F6F\u4EF6\u6BCF\u4E00\u4E2A\u6539\u52A8\u90FD\u8981\u5C3D\u5FEB\u90E8\u7F72\u5230\u4EA7\u54C1\u73AF\u5883\u4E2D\uFF0C\u5B83\u6307\u7684\u662F\u4EFB\u4F55\u7684\u4EE3\u7801\u4FEE\u6539\u90FD\u53EF\u4EE5\u5728\u4EFB\u4F55\u65F6\u5019\u5B9E\u65BD\u90E8\u7F72\uFF1B\u6301\u7EED\u4EA4\u4ED8\u8868\u793A\u7684\u662F\u4E00\u79CD\u80FD\u529B\uFF0C\u800C\u6301\u7EED\u90E8\u7F72\u8868\u793A\u7684\u5219\u4E00\u79CD\u65B9\u5F0F\u3002\u6301\u7EED\u90E8\u7F72\u662F\u6301\u7EED\u4EA4\u4ED8\u7684\u6700\u9AD8\u9636\u6BB5</p><h2 id="ci\xB7cd\u3001devops-\u5173\u7CFB" tabindex="-1">CI\xB7CD\u3001DevOps \u5173\u7CFB <a class="header-anchor" href="#ci\xB7cd\u3001devops-\u5173\u7CFB" aria-hidden="true">#</a></h2><p>DevOps \u662F CI\xB7CD \u601D\u60F3\u7684\u5EF6\u4F38\uFF0CCI\xB7CD \u662F DevOps \u7684\u57FA\u7840\u6838\u5FC3\u3002</p><p>DevOps\uFF08Development \u548C Operations\uFF09\u662F\u4E00\u79CD\u601D\u60F3\uFF0C\u662F\u4E00\u79CD\u6587\u5316\uFF0C\u4E3B\u8981\u5F3A\u8C03\u8F6F\u4EF6\u5F00\u53D1\u6D4B\u8BD5\u8FD0\u7EF4\u7684\u4E00\u4F53\u5316\uFF0C\u76EE\u6807\u662F\u51CF\u5C11\u5404\u4E2A\u90E8\u95E8\u4E4B\u95F4\u7684\u6C9F\u901A\u6210\u672C\u4ECE\u800C\u5B9E\u73B0\u8F6F\u4EF6\u7684\u5FEB\u901F\u9AD8\u8D28\u91CF\u7684\u53D1\u5E03\u3002\u5982\u6253\u901A\u7528\u6237\u3001PMO\u3001\u9700\u6C42\u3001\u8BBE\u8BA1\u3001\u5F00\u53D1\uFF08Dev\uFF09\u3001\u6D4B\u8BD5\u3001\u8FD0\u7EF4\uFF08Ops\uFF09\u7B49\u5404\u4E0A\u4E0B\u6E38\u90E8\u95E8\u6216\u4E0D\u540C\u89D2\u8272\u3001\u6253\u901A\u4E1A\u52A1\u3001\u67B6\u6784\u3001\u4EE3\u7801\u3001\u6D4B\u8BD5\u3001\u90E8\u7F72\u3001\u76D1\u63A7\u3001\u5B89\u5168\u3001\u6027\u80FD\u7B49\u5404\u9886\u57DF\u5DE5\u5177\u94FE\u3001\u5C24\u5176\u662F\u6253\u901A\u5F00\u53D1\u4E0E\u8FD0\u7EF4\u4E4B\u95F4\u7684\u9694\u9602\uFF0C\u56E0\u4E3A\u4E24\u8005\u5B9E\u9645\u4E0A\u5B58\u5728\u7740\u5F88\u591A\u7684\u51B2\u7A81\u3002</p><p>CI\xB7CD\uFF08Continuous Integration \u6301\u7EED\u96C6\u6210 \u548C Continuous Delivery \u6301\u7EED\u4EA4\u4ED8\uFF09\u662F\u6307\u6301\u7EED\u96C6\u6210\u53D1\u5E03\u90E8\u7F72\uFF0C\u662F\u4E00\u5957\u6D41\u7A0B\u5B9E\u73B0\u8F6F\u4EF6\u7684\u6784\u5EFA\u6D4B\u8BD5\u90E8\u7F72\u7684\u81EA\u52A8\u5316\u3002</p><p>DevOps \u4E0E cicd \u7D27\u5BC6\u76F8\u5173\uFF0C\u662F\u7406\u8BBA\u4E0E\u5B9E\u8DF5\u7684\u7ED3\u5408\uFF0CDevOps \u8981\u5B9E\u73B0\u4E00\u4F53\u5316\uFF0C\u5FC5\u987B\u8981\u501F\u52A9 CI\xB7CD \u5DE5\u5177\u6765\u81EA\u52A8\u5316\u6574\u4E2A\u6D41\u7A0B\u3002</p>',23),n=[t];function p(o,r,c,h,l,C){return i(),a("div",null,n)}var u=e(d,[["render",p]]);export{v as __pageData,u as default};
